import React, { useState } from 'react';
import Register from './components';
import Login from './components/login';

const App = () => {
  const [showSignUp, setShowSignUp] = useState(false);
  const [showSignIn, setShowSignIn] = useState(false);
  const [userType, setUserType] = useState('');

  const handleSignUpClick = () => {
    setShowSignUp(true);
    setShowSignIn(false);
  };

  const handleSignInClick = () => {
    setShowSignIn(true);
    setShowSignUp(false);
  };

  const handleUserTypeClick = (type) => {
    setUserType(type);
  };

  return (
    <div>
      {!showSignUp && !showSignIn && (
        <div>
          <h2>Welcome! Choose an option:</h2>
          <button onClick={handleSignUpClick}>Sign Up</button>
          <button onClick={handleSignInClick}>Sign In</button>
        </div>
      )}

      {showSignUp && !showSignIn && (
        <div>
          <h2>Choose your role:</h2>
          <button onClick={() => handleUserTypeClick('user')}>User</button>
          <button onClick={() => handleUserTypeClick('admin')}>Admin</button>
        </div>
      )}

      {showSignUp && showSignIn && userType === 'user' && <Register />}
      {showSignUp && showSignIn && userType === 'admin' && <Register />}

      {!showSignUp && showSignIn && userType === 'user' && <Login />}
      {!showSignUp && showSignIn && userType === 'admin' && <Login />}
    </div>
  );
};

export default App;
