// TransactionForm.js

import React, { useState } from 'react';


const TransactionForm = () => {
  const [amount, setAmount] = useState('');
  const [description, setDescription] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Prepare transaction data
    const transactionData = {
      amount: amount,
      description: description,
      user_id: 1 // Replace with the actual user ID
      // Add other fields as needed
    };

    try {
      // Send transaction data to the Flask backend
      const response = await fetch('/transactions', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(transactionData)
      });

      if (!response.ok) {
        throw new Error('Network response was not ok');
      }

      const data = await response.json();
      console.log('Transaction added successfully:', data);



    } catch (error) {
      console.error('Error adding transaction:', error);

    }
  };
  useEffect(() => {
    const fetchTransactions = async () => {
      try {
        setLoading(true);

        // Fetch transactions data from the backend for the specific account
        const response = await fetch(`/user/${userId}/accounts/${accountId}/transactions`, {
          method: 'GET',
          headers: {
            'Content-Type': 'application/json',
          },
        });

        if (response.ok) {
          const transactionsData = await response.json();
          console.log('Transactions data:', transactionsData);

          // Update the transactions state
          setTransactions(transactionsData.transactions);
        } else {
          console.error('Error fetching transactions. Status:', response.status);
          const errorMessage = await response.text();
          console.error('Error message:', errorMessage);
          setError(`Error fetching transactions: ${errorMessage}`);
        }
      } catch (error) {
        console.error('Error fetching transactions:', error);
        setError('Something went wrong. Please try again.');
      } finally {
        setLoading(false);
      }
    };

    fetchTransactions();
  }, [userId, accountId]);

  return (
    <form onSubmit={handleSubmit}>
      <label htmlFor="amount">Amount:</label>
      <input
        type="number"
        id="amount"
        name="amount"
        value={amount}
        onChange={(e) => setAmount(e.target.value)}
        required
      />

      <label htmlFor="description">Description:</label>
      <input
        type="text"
        id="description"
        name="description"
        value={description}
        onChange={(e) => setDescription(e.target.value)}
        required
      />

      {/* Add other form fields as needed */}

      <button type="submit">Submit</button>
    </form>
  );
};

export default TransactionForm;
