import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import DeleteUser from './DeleteUser';

const AdminDashboard = () => {
  const [usersData, setUsersData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Define the fetchUsersData function outside useEffect
  const fetchUsersData = async () => {
    try {
      const response = await fetch('/users');

      if (response.ok) {
        const data = await response.json();

        if (Array.isArray(data)) {
          setUsersData(data);
        } else if (data.users) {
          setUsersData(data.users);
        } else {
          setError('Invalid data format received.');
        }
      } else {
        setError('Failed to fetch users data.');
      }
    } catch (error) {
      setError('Error fetching users data.');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    // Call fetchUsersData inside useEffect
    fetchUsersData();
  }, []);

  const handleDeleteUser = async (userId) => {
    try {
      setLoading(true);
      const response = await fetch(`/user/${userId}`, {
        method: 'DELETE',
        headers: {
          'Content-Type': 'application/json',
        },
      });

      if (response.ok) {
        // Reload users data after successful deletion
        fetchUsersData();
      } else {
        const data = await response.json();
        setError(data.error || 'Failed to delete user.');
      }
    } catch (error) {
      console.error('Error deleting user:', error);
      setError('Internal Server Error. Please try again later.');
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return <p>Loading...</p>;
  }

  if (error) {
    return <p style={{ color: 'red' }}>{error}</p>;
  }

  return (
    <div>
      <h1>Admin Dashboard</h1>
      <h2>Clients:</h2>
      {Array.isArray(usersData) && usersData.length > 0 ? (
        usersData.map((user) => (
          <div key={user.id}>
            <h3>Username: {user.username}</h3>
            <p>Name: {`${user.first_name || 'N/A'} ${user.last_name || 'N/A'}`}</p>
            <p>Email: {user.email || 'N/A'}</p>

            {/* Add Delete User button with onClick event */}
            <button onClick={() => handleDeleteUser(user.id)}>Delete User</button>

            <DeleteUser
                userId={userId}
                accountId={account.id}
                onDeleteSuccess={handleDeleteAccount}
              />

            {user.accounts && user.accounts.length > 0 && (
              <div>
                <h4>User Accounts</h4>
                {user.accounts.map((account) => (
                  <div key={account.id}>
                    <p>Account ID: {account.id}</p>
                    <p>Balance: ${account.balance}</p>
                  </div>
                ))}
              </div>
            )}

            {user.transactions && user.transactions.length > 0 && (
              <div>
                <h4>User Transactions</h4>
                {user.transactions.map((transaction) => (
                  <div key={transaction.id}>
                    <p>Transaction ID: {transaction.id}</p>
                    <p>Amount: ${transaction.amount}</p>
                  </div>
                ))}
              </div>
            )}
            <hr />
          </div>
        ))
      ) : (
        <p>No users data available.</p>
      )}
    </div>
  );
};

export default AdminDashboard;
