import React, { useState, useEffect, useContext } from 'react';
import UserContext from '/U'

const UserDashboard = () => {
  const { user } = useContext(UserContext); // Assuming UserContext provides user data

  const [userData, setUserData] = useState(null);
  const [error, setError] = useState(null);

  const fetchUserData = async () => {
    try {
      // Assuming you have the user ID in state (replace with your actual state variable)
      const userId = user.id;  // Replace with the actual property that holds the user ID

      // Make sure the user ID is defined before making the request
      if (!userId) {
        throw new Error('User ID is undefined');
      }

      const response = await fetch(`/users/${userId}`);

      if (!response.ok) {
        throw new Error(`Error fetching user data: ${response.status} - ${response.statusText}`);
      }

      const data = await response.json();
      setUserData(data.user);
    } catch (error) {
      console.error('Fetch user data error:', error);
      setError(error.message);
    }
  };

  useEffect(() => {
    fetchUserData();
  }, []);  // Add dependencies if needed

  return (
    <div>
      {userData ? (
        <div>
          <h2>Welcome, {userData.username}!</h2>
          {/* Render other user data as needed */}
        </div>
      ) : (
        <p>Loading user data...</p>
      )}

      {error && <p>Error: {error}</p>}
    </div>
  );
};

export default UserDashboard;
