import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';

const UserDashboard = () => {
  const { user_Id } = useParams();
  const [userData, setUserData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchUserData = async () => {
      try {
        // Check if userId is defined
        if (!user_Id) {
          throw new Error('User ID is undefined.');
        }

        const response = await fetch(`/user/${user_Id}`);

        if (!response.ok) {
          const errorMessage = await response.text();
          throw new Error(`Failed to fetch user data. Status: ${response.status}, Message: ${errorMessage}`);
        }

        const data = await response.json();

        // Check if the response has the expected structure
        if (!data || !data.id || !data.accounts || !data.transactions) {
          console.error('Invalid response structure. Response:', data);
          throw new Error('Invalid response structure.');
        }

        setUserData(data);
      } catch (error) {
        console.error('Error fetching user data:', error.message);
        setError(`Failed to fetch user data. ${error.message}`);
      } finally {
        setLoading(false);
      }
    };

    fetchUserData();
  }, [userId]);

  if (!userId) {
    return <p style={{ color: 'red' }}>User ID is missing. Please provide a valid user ID.</p>;
  }

  if (loading) {
    return <p>Loading...</p>;
  }

  if (error) {
    if (error.includes('User not found')) {
      return <p style={{ color: 'red' }}>Sorry, the user you are looking for was not found. Please check the user ID and try again.</p>;
    } else {
      return <p style={{ color: 'red' }}>Failed to fetch user data. Please try again later.</p>;
    }
  }

  return (
    <div>
      <h2>User Dashboard</h2>

      {/* Display user details */}
      <div>
        <h3>User Details</h3>
        <p>ID: {userData.id}</p>
        <p>First Name: {userData.first_name}</p>
        <p>Last Name: {userData.last_name}</p>
        <p>Username: {userData.username}</p>
        <p>Email: {userData.email}</p>
      </div>

      {/* Display user accounts */}
      <div>
        <h3>User Accounts</h3>
        {userData.accounts && userData.accounts.length > 0 ? (
          userData.accounts.map((account) => (
            <div key={account.id}>
              <p>Account ID: {account.id}</p>
              <p>Balance: ${account.balance}</p>
            </div>
          ))
        ) : (
          <p>No accounts found for this user.</p>
        )}
      </div>

      {/* Display user transactions */}
      <div>
        <h3>User Transactions</h3>
        {userData.transactions && userData.transactions.length > 0 ? (
          userData.transactions.map((transaction) => (
            <div key={transaction.id}>
              <p>Transaction ID: {transaction.id}</p>
              <p>Amount: ${transaction.amount}</p>
            </div>
          ))
        ) : (
          <p>No transactions found for this user.</p>
        )}
      </div>
    </div>
  );
};

export default UserDashboard;
