// In your UserDashboard.js or UserDashboard.jsx file
import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import CreateAccount from './CreateAccount'; // Import the CreateAccount component

const UserDashboard = ({ userId }) => {
    const [userData, setUserData] = useState(null);

    // Fetch user data when the component mounts
    useEffect(() => {
        const fetchUserData = async () => {
            try {
                const response = await fetch(`/user/${userId}`);
                if (response.ok) {
                    const data = await response.json();
                    setUserData(data.user);
                } else {
                    console.error('Error fetching user data. Status:', response.status);
                }
            } catch (error) {
                console.error('Error during fetchUserData:', error);
            }
        };

        fetchUserData();
    }, [userId]);

    return (
        <div>
            {userData ? (
                <>
                    <h2>Welcome, {userData.username}!</h2>
                    {/* Display user data, e.g., userData.first_name, userData.last_name, etc. */}

                    <Link to={`/user/${userId}/accounts`}>
                        <button>View Accounts</button>
                    </Link>

                    {/* Add a link to create a new account */}
                    <Link to={`/user/${userId}/create-account`}>
                        <button>Create New Account</button>
                    </Link>

                    {/* Add route for creating a new account */}
                    {/* <Route path="/user/:userId/create-account" component={CreateAccount} /> */}
                    {/* Uncomment the above line if you are using React Router Routes */}
                </>
            ) : (
                <p>Loading user data...</p>
            )}
        </div>
    );
};

export default UserDashboard;
