import React, { useEffect, useState } from 'react';
import { useParams, Link, Route } from 'react-router-dom';
import CreateAccount from './CreateAccount'; // Import the CreateAccount component

const UserDashboard = () => {
  const { userId } = useParams();
  const [userData, setUserData] = useState(null);

  // Assuming you have a function to fetch user data, like fetchUserData
  useEffect(() => {
    const fetchUserData = async () => {
      try {
        const response = await fetch(`/user/${userId}`);
        if (response.ok) {
          const data = await response.json();
          setUserData(data.user);
        } else {
          console.error('Error fetching user data:', response.statusText);
        }
      } catch (error) {
        console.error('Error fetching user data:', error);
      }
    };

    fetchUserData();
  }, [userId]);

  return (
    <div>
      {/* Display user data, e.g., userData.first_name, userData.last_name, etc. */}
      {userData && (
        <>
          <h2>User Dashboard</h2>
          <p>
            Name: {userData.first_name} {userData.last_name}
          </p>
          <p>Email: {userData.email}</p>

          {/* Link to view accounts */}
          <Link to={`/user/${userId}/accounts`}>
            <button>View Accounts</button>
          </Link>

          {/* Link to create a new account */}
          <Link to={`/user/${userId}/create-account`}>
            <button>Create New Account</button>
          </Link>

          {/* React Router Routes */}
          <Route
            path="/user/:userId/create-account"
            render={(props) => <CreateAccount {...props} userId={userId} />}
          />
        </>
      )}
    </div>
  );
};

export default UserDashboard;
