// App.js

import React, { useState } from 'react';
import SignUpForm from './components/signUpForm';
import SignInForm from './components/signInForm';


function App() {
  const [userType, setUserType] = useState('User');
  const [isSignIn, setIsSignIn] = useState(false);

  const handleSignInClick = () => {
    setIsSignIn(true);
  };

  const handleSignUpClick = () => {
    setIsSignIn(false);
  };

  return (
    <div>
      <h1>Bank App</h1>
      <div>
        <button onClick={() => setUserType('User')} disabled={isSignIn}>
          Sign Up as User
        </button>
        <button onClick={() => setUserType('Admin')} disabled={isSignIn}>
          Sign Up as Admin
        </button>
        <button onClick={handleSignUpClick} disabled={!isSignIn}>
          Sign In
        </button>
      </div>
      {isSignIn ? (
        <SignInForm userType={userType} />
      ) : (
        <SignUpForm userType={userType} />
      )}
    </div>
  );
}

export default App;
