import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';

const UserDashboard = () => {
  const { userId } = useParams();
  const [allUsers, setAllUsers] = useState([]);
  const [loggedInUser, setLoggedInUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchAllUsers = async () => {
      try {
        const response = await fetch('/users');

        if (!response.ok) {
          throw new Error(`Failed to fetch user list. Status: ${response.status}`);
        }

        const usersData = await response.json();
        setAllUsers(usersData);

        // Find the logged-in user based on userId
        const foundUser = usersData.find(user => user.id === userId);

        if (!foundUser) {
          throw new Error('User not found.');
        }

        setLoggedInUser(foundUser);
      } catch (error) {
        console.error('Error fetching user data:', error.message);
        setError(`Failed to fetch user data. ${error.message}`);
      } finally {
        setLoading(false);
      }
    };

    fetchAllUsers();
  }, [userId]);

  if (!userId) {
    return <p style={{ color: 'red' }}>User ID is missing. Please provide a valid user ID.</p>;
  }

  if (loading) {
    return <p>Loading...</p>;
  }

  if (error) {
    if (error.includes('User not found')) {
      return <p style={{ color: 'red' }}>Sorry, the user you are looking for was not found. Please check the user ID and try again.</p>;
    } else {
      return <p style={{ color: 'red' }}>Failed to fetch user data. Please try again later.</p>;
    }
  }

  return (
    <div>
      <h2>User Dashboard</h2>

      {/* Display user details */}
      <div>
        <h3>User Details</h3>
        <p>ID: {loggedInUser.id}</p>
        <p>First Name: {loggedInUser.first_name}</p>
        <p>Last Name: {loggedInUser.last_name}</p>
        <p>Username: {loggedInUser.username}</p>
        <p>Email: {loggedInUser.email}</p>
      </div>

      {/* Additional sections for displaying accounts and transactions if needed */}
    </div>
  );
};

export default UserDashboard;
