import React, { useState } from 'react';
import { Link } from 'react-router-dom';

const HomePage = () => {
  const [selectedRole, setSelectedRole] = useState(null);

  return (
    <div style={{ textAlign: 'center' }}>
      {/* Display the image from an online link */}
      <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '200px' }}>
        <img src="https://example.com/path/to/your/image.jpg" alt="Vaulted Wealth Logo" style={{ maxWidth: '100%', height: 'auto' }} />
      </div>

      <h1>Welcome to Vaulted Wealth</h1>

      <div>
        <h2>Select Role:</h2>

        <p>
          <button
            style={{ marginRight: '10px' }}
            onClick={() => setSelectedRole('user')}
          >
            <h1>User</h1>
          </button>

          <button
            style={{ marginLeft: '10px' }}
            onClick={() => setSelectedRole('admin')}
          >
            <h2>Admin</h2>
          </button>
        </p>
      </div>

      {selectedRole && (
        <div>
          <h2>Select Action:</h2>

          {/* Conditionally render links based on the selected role */}
          {selectedRole === 'user' && (
            <>
              <Link to="/signup">
                <button>Sign Up</button>
              </Link>
              <Link to="/login">
                <button>Login</button>
              </Link>
            </>
          )}

          {selectedRole === 'admin' && (
            <>
              <Link to="/admin/signup">
                <button>Admin Sign Up</button>
              </Link>
              <Link to="/admin/login">
                <button>Admin Login</button>
              </Link>
            </>
          )}
        </div>
      )}
    </div>
  );
};

export default HomePage;
