const AdminSignupPage = () => {
    const [formData, setFormData] = useState({
      first_name: '',
      last_name: '',
      username: '',
      password: '',
      email: '',
    });

    const [errorMessage, setErrorMessage] = useState('');

    const handleChange = (e) => {
      setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
      e.preventDefault();

      try {
        const response = await fetch('/admin/signup', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify(formData),
        });

        console.log('Response Content:', await response.text());

        if (response.ok) {
          // Admin signup successful, redirect to admin login page
          navigate('/admin/login');
        } else {
          // Admin signup failed, update error message
          const errorResponse = await response.json();
          setErrorMessage(errorResponse.message || 'Admin signup failed');
        }
      } catch (error) {
        console.error('Error during admin signup', error);
        setErrorMessage('An error occurred during admin signup');
      }
    };

    return (
        <div>
        <h2>Admin Signup</h2>
        <form onSubmit={handleSubmit}>
          {/* Add input fields for admin signup */}
          <label>
            First Name:
            <input type="text" name="first_name" value={formData.first_name} onChange={handleChange} />
          </label>
          <br />
          <label>
            Last Name:
            <input type="text" name="last_name" value={formData.last_name} onChange={handleChange} />
          </label>
          <br />
          <label>
            Username:
            <input type="text" name="username" value={formData.username} onChange={handleChange} />
          </label>
          <br />
          <label>
            Password:
            <input type="password" name="password" value={formData.password} onChange={handleChange} />
          </label>
          <br />
          <label>
            Email:
            <input type="email" name="email" value={formData.email} onChange={handleChange} />
          </label>
          <br />
          <button type="submit">Sign Up</button>
        </form>

        {errorMessage && <p>{errorMessage}</p>}
      </div>
    );
  };

  export default AdminSignupPage;
