// src/components/UserDashboard.js
import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';

const UserDashboard = () => {
  const { userId } = useParams();
  const [loggedInUser, setLoggedInUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchAllUsers = async () => {
      try {
        const response = await fetch('/users');

        if (!response.ok) {
          throw new Error(`Failed to fetch user list. Status: ${response.status}`);
        }

        const responseData = await response.json();

        if (!responseData.users || !Array.isArray(responseData.users)) {
          console.error('Invalid response structure. Expected an array of users. Response:', responseData);
          throw new Error('Invalid response structure. Expected an array of users.');
        }

        const usersData = responseData.users;

        // Log the userId and usersData for debugging
        console.log('Cleaned UserID:', userId);
        console.log('Users Data:', usersData);

        // Find the logged-in user based on username
        const foundUser = usersData.find(user => user.username === userId);

        if (!foundUser) {
          console.error('User not found. Response:', usersData);
          setError('User not found. Please check the username and try again.');
          return;
        }

        setLoggedInUser(foundUser);
      } catch (error) {
        console.error('Error fetching user data:', error.message);
        setError(`Failed to fetch user data. ${error.message}`);
      } finally {
        setLoading(false);
      }
    };

    if (userId) {
      fetchAllUsers();
    } else {
      setError('Username is missing. Please provide a valid username.');
      setLoading(false);
    }
  }, [userId]);

  if (loading) {
    return <p>Loading...</p>;
  }

  if (error) {
    return <p style={{ color: 'red' }}>{error}</p>;
  }

  return (
    <div>
      <h2>User Dashboard</h2>

      {/* Display user details */}
      <div>
        <h3>User Details</h3>
        <p>ID: {loggedInUser.id}</p>
        <p>First Name: {loggedInUser.first_name}</p>
        <p>Last Name: {loggedInUser.last_name}</p>
        <p>Username: {loggedInUser.username}</p>
        <p>Email: {loggedInUser.email}</p>
      </div>
    </div>
  );
};

export default UserDashboard;
