import React, { useState, useEffect } from 'react';

const UserDashboard = () => {
  const [userId, setUserId] = useState(null);
  const [userData, setUserData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const login = async () => {
    // Assume your login logic here and get the user's ID
    const userLoginResponse = await fetch('/login', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(/* your login data */),
    });

    if (userLoginResponse.ok) {
      const userData = await userLoginResponse.json();
      setUserId(userData.id);
    } else {
      setError('Login failed. Please try again.');
    }
  };

  const fetchUserById = async () => {
    try {
      if (!userId) {
        return; // userId is undefined, don't make the request
      }

      const response = await fetch(`/users/${userId}`, {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
        },
      });

      if (response.ok) {
        const data = await response.json();
        setUserData(data.user);
      } else {
        const errorMessage = await response.text();
        setError(errorMessage || 'Error fetching user.');
      }
    } catch (error) {
      console.error('Error fetching user:', error);
      setError('Something went wrong. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    login(); // Call your login function to get the user's ID
  }, []);

  useEffect(() => {
    fetchUserById();
  }, [userId]);

  return (
    <div>
      <h1>User Dashboard</h1>
      {loading && <p>Loading user data...</p>}
      {error && <p>{error}</p>}
      {userData && (
        <div>
          <h2>User Information</h2>
          <p>ID: {userData.id}</p>
          <p>First Name: {userData.first_name}</p>
          <p>Last Name: {userData.last_name}</p>
          <p>Username: {userData.username}</p>
          <p>Email: {userData.email}</p>
        </div>
      )}
    </div>
  );
};

export default UserDashboard;
