// Login.js

import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import UserDashboard from './UserDashboard';

const Login = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    username: '',
    password: '',
  });
  const [loading, setLoading] = useState(false);
  const [errorMessage, setErrorMessage] = useState('');
  const [userData, setUserData] = useState(null);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleLoginSuccess = (userData) => {
    setUserData(userData);
    navigate('/dashboard'); // Redirect to the user dashboard
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      setLoading(true);
      setErrorMessage('');

      const response = await fetch('/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });

      const data = await response.json();

      if (response.ok) {
        console.log('Login successful:', data);

        if (!data.user || !data.user.id) {
          setErrorMessage('User ID not found in the response.');
          return;
        }

        // Fetch user data after successful login
        const userResponse = await fetch(`/user/${data.user.id}`, {
          method: 'GET',
          headers: {
            'Content-Type': 'application/json',
          },
        });

        if (userResponse.ok) {
          const userData = await userResponse.json();
          console.log('User data:', userData);

          // Update the user data state and navigate to the user dashboard
          handleLoginSuccess(userData);
        } else {
          console.error('Error fetching user data:', userResponse.statusText);

          if (userResponse.status === 500) {
            setErrorMessage('Internal Server Error. Please try again later.');
          } else {
            setErrorMessage('Error fetching user data.');
          }
        }
      } else {
        console.error('Login failed:', data.message);
        setErrorMessage(data.message || 'Login failed.');
      }
    } catch (error) {
      console.error('Login failed', error);
      setErrorMessage('Something went wrong. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <h2>Login</h2>
      <form onSubmit={handleSubmit}>
        <label>
          Username:
          <input
            type="text"
            name="username"
            value={formData.username}
            onChange={handleChange}
          />
        </label>
        <br />
        <label>
          Password:
          <input
            type="password"
            name="password"
            value={formData.password}
            onChange={handleChange}
          />
        </label>
        <br />
        <button type="submit" disabled={loading}>
          {loading ? 'Logging in...' : 'Login'}
        </button>
        {errorMessage && <p style={{ color: 'red' }}>{errorMessage}</p>}
      </form>

      {userData && <UserDashboard userData={userData} />}
    </div>
  );
};

export default Login;
