// UserDashboard.js

import React, { useState, useEffect } from 'react';

const UserDashboard = ({ userData }) => {
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [allUsers, setAllUsers] = useState([]);
  const [loggedInUserData, setLoggedInUserData] = useState(null);

  useEffect(() => {
    const fetchAllUsers = async () => {
      try {
        setLoading(true);

        const response = await fetch('/users', {
          method: 'GET',
          headers: {
            'Content-Type': 'application/json',
          },
        });

        if (response.ok) {
          const usersData = await response.json();
          console.log('All users data:', usersData);

          // Update the allUsers state
          setAllUsers(usersData.users);
        } else {
          console.error('Error fetching all users. Status:', response.status);
          const errorMessage = await response.text();
          console.error('Error message:', errorMessage);
          setError(`Error fetching all users: ${errorMessage}`);
        }
      } catch (error) {
        console.error('Error fetching all users:', error);
        setError('Something went wrong. Please try again.');
      } finally {
        setLoading(false);
      }
    };

    fetchAllUsers();
  }, []);

  useEffect(() => {
    console.log('userData:', userData);
    console.log('allUsers:', allUsers);

    // Compare the logged-in user ID with all users and update loggedInUserData
    if (userData && allUsers.length > 0) {
      const loggedInUser = allUsers.find(user => user.id === userData.id);
      console.log('loggedInUser:', loggedInUser);

      if (loggedInUser) {
        setLoggedInUserData(loggedInUser);
      } else {
        console.error('Logged-in user not found among all users.');
        // Handle this case, e.g., set an error state or redirect to login.
      }
    }
  }, [userData, allUsers]);

  return (
    <div>
      <h1>User Dashboard</h1>
      {loading && <p>Fetching user data...</p>}
      {error && <p>{error}</p>}
      {loggedInUserData && (
        <div>
          <h2>User Information</h2>
          <p>ID: {loggedInUserData.id}</p>
          <p>First Name: {loggedInUserData.first_name}</p>
          <p>Last Name: {loggedInUserData.last_name}</p>
          <p>Username: {loggedInUserData.username}</p>
          <p>Email: {loggedInUserData.email}</p>
        </div>
      )}
    </div>
  );
};

export default UserDashboard;
