import React, { useState, useEffect } from 'react';

const UserDashboard = ({ userData }) => {
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const fetchUserById = async () => {
    if (userData) {
      try {
        setLoading(true); // Move loading state update here

        const response = await fetch(`/user/${userData.id}`, {
          method: 'GET',
          headers: {
            'Content-Type': 'application/json',
          },
        });

        if (response.ok) {
          const data = await response.json();
          console.log('User data:', data.user);
          // Further processing with 'data'
        } else {
          const errorMessage = await response.text(); // Get detailed error message from the server
          console.error('Error fetching user data:', errorMessage);
          setError(`Error fetching user data: ${errorMessage}`);
        }
      } catch (error) {
        console.error('Error fetching user:', error);
        setError('Something went wrong. Please try again.');
      } finally {
        setLoading(false);
      }
    }
  };

  useEffect(() => {
    fetchUserById();
  }, [userData]);

  return (
    <div>
      <h1>User Dashboard</h1>
      {loading && <p>Fetching user data...</p>}
      {error && <p>{error}</p>}
      {userData && (
        <div>
          <h2>User Information</h2>
          <p>ID: {userData.id}</p>
          <p>First Name: {userData.first_name}</p>
          <p>Last Name: {userData.last_name}</p>
          <p>Username: {userData.username}</p>
          <p>Email: {userData.email}</p>
        </div>
      )}
    </div>
  );
};

export default UserDashboard;
